        private byte[] Encrypt(byte[] Data) => Decrypt(Data);

        private byte[] Decrypt(byte[] Data) {
            bool Switch = false;
            byte[] Out = new byte[Data.Length];
            Data.CopyTo(Out, 0);
            const uint K1 = 0x7190c70e, K2 = 0x499bf135;

            uint Key = Switch ? K2 : K1, Cnt1 = 0, SwitchTableBase = 0;

            XOR(ref Out, Switch ? K1 : K2);

            uint EAX, EBX, ECX, EDX, EDI = 0, ESI = 0, bak1, bak2;
            ECX = (uint)Data.LongLength / 64;

            CntLoop:;
            bak2 = ECX;
            ECX = 0x10;
            EAX = 0;
            Cnt1 = EAX;
            CpyCnt:;

            EBX = SwitchTableBase;
            EBX += Cnt1;
            EAX = 0;
            EAX = XORTBL[EBX];

            if (Switch)
                EAX = (~EAX) & 0xFF;

            bak1 = ECX;
            ECX = 0;
            EAX <<= (int)(ECX & 0xFF);
            ECX = bak1;
            EAX += 0;

            if (!(GetSigned(EAX) >= 0))
                EAX = 0;
            else if (!(GetSigned(EAX) <= 0xFF))
                EAX = 0xFF;

            EBX = EAX;
            EBX &= 0xFF;

            if (EBX != 0x00) {
                EBX <<= 2;
                SelectDWTable(ref EBX);
                EBX += 0x3FC;
                EBX = BitConverter.ToUInt32(DWTBL, (int)EBX);
                EBX &= 0xFF;
                if (EBX != 0x00) {
                    if (EBX != 0xFF) {
                        EBX <<= 2;
                        SelectDWTable(ref EBX);
                        EDX = BitConverter.ToUInt32(Data, (int)EBX);
                        EBX = 0;
                        EBX = (EBX & 0xFFFFFF00) | (Data[ESI]);
                        EAX = 0;
                        EAX = (EAX & 0xFFFFFF00) | (Data[EDI]);
                        EBX -= EAX;
                        EBX <<= 2;
                        EBX += EDX;
                        EAX += BitConverter.ToUInt32(Data, (int)EBX);
                        Out[EDI] = (byte)(EAX & 0xFF);

                        EBX = 0;
                        EBX = (EBX & 0xFFFFFF00) | (Data[ESI+1]);
                        EAX = 0;
                        EAX = (EAX & 0xFFFFFF00) | (Data[EDI+1]);
                        EBX -= EAX;
                        EBX <<= 2;
                        EBX += EDX;
                        EAX += BitConverter.ToUInt32(Data, (int)EBX);
                        Out[EDI+1] = (byte)(EAX & 0xFF);

                        EBX = 0;
                        EBX = (EBX & 0xFFFFFF00) | (Data[ESI+2]);
                        EAX = 0;
                        EAX = (EAX & 0xFFFFFF00) | (Data[EDI+2]);
                        EBX -= EAX;
                        EBX <<= 2;
                        EBX += EDX;
                        EAX += BitConverter.ToUInt32(Data, (int)EBX);
                        Out[EDI+2] = (byte)(EAX & 0xFF);
                    } else {
                        EAX = BitConverter.ToUInt32(Data, (int)ESI) ^ Key;
                        BitConverter.GetBytes(EAX).CopyTo(Out, (int)EDI);
                    }
                }
            }
            ESI += 4;
            EDI += 4;
            EAX = Cnt1;
            EAX++;
            if (!(EAX < 5)) {
                EAX = 0;
            }

            Cnt1 = EAX;
            ECX--;
            if (ECX != 0)
                goto CpyCnt;

            EDI += 0;
            ESI += 0;
            EAX = SwitchTableBase;
            EAX += 5;
            if (!(EAX < XORTBL.Length))
                EAX = 0;
            SwitchTableBase = EAX;
            ECX = bak2;
            ECX--;

            if (ECX != 0)
                goto CntLoop;

            return Out;
        }

        private int GetSigned(uint Val) {
            return BitConverter.ToInt32(BitConverter.GetBytes(Val), 0);
        }
        private void SelectDWTable(ref uint Seed) {
            Seed /= 4;

            DWTBL = new byte[DWTBL.Length];
            for (uint i = ((uint)DWTBL.LongLength/4) - 1; i > 0; i--) {
                BitConverter.GetBytes(Seed).CopyTo(DWTBL, i * 4);
                if (Seed > 0)
                    Seed--;
            }
        }
        private void FakeCompress(ref byte[] Data) {
            CompressionHeader Header = new CompressionHeader() {
                Magic = 1,
                dLen = (uint)Data.LongLength
            };

            List<byte> Buffer = new List<byte>();

            for (uint i = 0, x = 0; i < Data.Length; i++, x--) {
                if (x == 0) {
                    x = 8;
                    Buffer.Add(0xFF);
                }
                Buffer.Add(Data[i]);
            }

            Header.cLen = (uint)Buffer.LongCount() + 0x8;

            Buffer.InsertRange(0, Tools.BuildStruct(ref Header));

            Data = null;
            Data = Buffer.ToArray();
        }

